    function currentSelected($type)
    {
        switch($type)
        {
            case 1: return 'Admin';
                    break;
            case 2: return 'SAST Officer';
                    break;
            case 3: return 'Faculty';
                    break;
            case 4: return 'Student';
                    break;
            case 5: return 'Dean';
                    break;

            default: return 'User';
        }
    }
    function checkDean($det)
    {
        if(!$det->faculties->isEmpty())
        {
            foreach($det->faculties as $facs)
            {
                if($facs->isDean)
                    return $facs;
            }
        }

        return null;
    }
    function checkAssDean($det)
    {
        if(!$det->faculties->isEmpty())
        {
            foreach($det->faculties as $facs)
            {
                if($facs->isAssDean)
                    return $facs;
            }
        }

        return null;
    }
    function checkChairman($det)
    {
        if(!$det->faculties->isEmpty())
        {
            foreach($det->faculties as $facs)
            {
                if($facs->isChairman)
                    return $facs;
            }
        }

        return null;
    }
